const initTheme=()=>{const e=localStorage.getItem("theme"),t=window.matchMedia("(prefers-color-scheme: dark)").matches,n=e||(t?"dark":"light");document.documentElement.setAttribute("data-theme",n)};initTheme(),document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("theme-toggle");if(!e)return;const t=e=>{document.documentElement.setAttribute("data-theme",e),localStorage.setItem("theme",e)};e.addEventListener("click",()=>{const n=document.documentElement.getAttribute("data-theme"),e=n==="dark"?"light":"dark";t(e),console.log("Theme switched to:",e)}),window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",e=>{localStorage.getItem("theme")||t(e.matches?"dark":"light")})})