function setTheme(e){document.documentElement.setAttribute("data-theme",e),localStorage.setItem("theme",e)}function toggleTheme(){const e=localStorage.getItem("theme")||"light",t=e==="light"?"dark":"light";setTheme(t)}document.addEventListener("DOMContentLoaded",()=>{const e=localStorage.getItem("theme")||"light";setTheme(e);const t=document.getElementById("theme-toggle");t.addEventListener("click",toggleTheme)})